    public void displayCustomerReviews(Vendor vendor) {
        model.setRowCount(0);
        this.vendorReview = null;
        clearReviewDetails();
        VendorReview review = new VendorReview(vendor);

        // Call getAverageRating method
        String averageRating = review.getAverageRating();
        
        // Set the result in your label
        lblAverageShopRating.setText(averageRating);
        
        try {
            double ratingValue = Double.parseDouble(averageRating);
            if(ratingValue >= 4.5 && ratingValue <= 5)
            {
                lblRemarks.setText("Excellent");
            }
            else if (ratingValue > 3.5 && ratingValue < 4.5)
            {
                lblRemarks.setText("Good");
            }
            else if (ratingValue > 1.5 && ratingValue < 3.5)
            {
                lblRemarks.setText("Below Average");
            }
            else if (ratingValue >= 0 && ratingValue < 1.5)
            {
                lblRemarks.setText("Poor");
            }
        } catch (NumberFormatException e) {
            lblRemarks.setText("-");
        }
        
        ArrayList<VendorReview> reviewList = review.getVendorReviewList();
        for (VendorReview vendorreview : reviewList) {
            String[] tableData = {vendorreview.getId(), vendorreview.getReviewDate(), String.valueOf(vendorreview.getReviewRating())};
            model.addRow(tableData);
        }
    }
    
    private void displayCustomerReviewDetails() {
        if (vendorReview != null) {
            lblReviewID.setText(vendorReview.getId());
            lblReviewDate.setText(vendorReview.getReviewDate());
            lblReviewRating.setText(String.valueOf(vendorReview.getReviewRating()));
            lblReviewCcomments.setText(vendorReview.getReviewComments());
        }
    }
    
    private void clearReviewDetails() {
        lblReviewID.setText("");
        lblReviewDate.setText("");
        lblReviewRating.setText("");
        lblReviewCcomments.setText("");
    }
    
    @Override
    public boolean comboboxFilterImplementation(RowFilter.Entry<? extends DefaultTableModel, ? extends Object> entry) {
        String ratingFilter = (String) cbxRating.getSelectedItem();
        String searchFilter = txtSearch.getText();
        
        String ratingItem = entry.getStringValue(2);
        String idItem = entry.getStringValue(0);
        
        boolean rating = "All".equals(ratingFilter) || ratingItem.equals(ratingFilter);
        boolean id = searchFilter.isEmpty() || searchFilter.isBlank() || idItem.contains(searchFilter);
                
        return rating && id;    
    }
    
