    private void cbxRatingItemStateChanged(java.awt.event.ItemEvent evt) {//GEN-FIRST:event_cbxRatingItemStateChanged
        applyFilters(sorter);
        clearReviewDetails();
    }//GEN-LAST:event_cbxRatingItemStateChanged

    private void txtSearchKeyTyped(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_txtSearchKeyTyped
        applyFilters(sorter);
        clearReviewDetails();
    }//GEN-LAST:event_txtSearchKeyTyped

    private void customerReviewTableMouseReleased(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_customerReviewTableMouseReleased
        int row = customerReviewTable.getSelectedRow();
        if (row >= 0) {
            String reviewID = String.valueOf(customerReviewTable.getValueAt(row, 0));
            vendorReview = (VendorReview) new VendorReview(reviewID).getObject("Vendor Reviews");
            displayCustomerReviewDetails();
        }
    }//GEN-LAST:event_customerReviewTableMouseReleased


